<!-- Custom JS -->
<script type="text/javascript">
  
  $( document ).ready(function() {
    // Validate if is a number
    function isNumber(n) {
      return !isNaN(parseInt(n)) && isFinite(n);
    }
    // End Validate if is a number
    
    // Add currency field validations
    function addCurrencyValidation(){
      // Remove duplicate keyup and change functions
      $('.currency').off('keyup');
      $('.currency').off('change');
      // End Remove duplicate keyup and change functions
      
      $('.currency').each(function(){
        // Validate numbers
        $(this).on('keyup change', function(){
          if ( !isNumber($(this).val()) ) {
            return false;
          }
        });
        // End Validate numbers
        
        // Avoid enter letters and symbols
        $(this).keypress(function(e) {
          if(e.charCode !== 46){ // Dot (.)
            if(e.charCode < 48 || e.charCode > 57){
              return false;
            }
          }
        });
        // End Avoid enter letters and symbols
      });
    }
    // End Add currency field validations

    // Add only-integer field validations
    function addOnlyIntegerValidation(){
      // Remove duplicate keyup and change functions
      $('.only-integer').off('keyup');
      $('.only-integer').off('change');
      // End Remove duplicate keyup and change functions
      
      $('.only-integer').each(function(){
        // Validate numbers
        $(this).on('keyup change', function(){
          if ( !isNumber($(this).val()) ) {
            return false;
          }
        });
        // End Validate numbers
        
        // Avoid enter letters and symbols
        $(this).keypress(function(e) {
            if(e.charCode < 48 || e.charCode > 57){
              return false;
            }
        });
        // End Avoid enter letters and symbols
      });
    }
    // End Add only-integer field validations
    
    // Init functions
    addCurrencyValidation();
    addOnlyIntegerValidation();
    // End Init functions
  });
  
</script>
<!-- End Custom JS -->


