<!-- Liquidacion JS -->
<script type="text/javascript">
  $(document).ready(function () {
    // Hide results
    $('#severance_pay_fields').hide();

    // Hide salario variable
    $('.salario-variable').hide();

    $('#modalida_salario').on('change', function () {
      if (this.value === 'variable') {
        $('.salario-fijo').hide();
        $('.salario-variable').show();
      } else {
        $('.salario-variable').hide();
        $('.salario-fijo').show();
      }
    });

    // Format Currency
    const formatter = new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
      minimumFractionDigits: 2,
    });

    // Datepicker
    $('#fecha_inicio')
      .datepicker({
        format: 'dd/mm/yyyy',
        weekStart: 0,
        endDate: 'today',
        todayBtn: true,
        language: 'es',
        autoclose: true,
        todayHighlight: true,
      })
      .on('changeDate', function (evt) {
        ConfigureToDate();
      });

    $('#fecha_fin').datepicker({
      format: 'dd/mm/yyyy',
      weekStart: 0,
      todayBtn: true,
      language: 'es',
      autoclose: true,
      todayHighlight: true,
      startDate: $('#fecha_inicio').val().trim(),
    });

    // Resets the min date of the return date
    function ConfigureToDate() {
      $('#fecha_fin').val('').datepicker('update');
      $('#fecha_fin').datepicker('setStartDate', $('#fecha_inicio').val().trim());
    }

    ConfigureToDate();
    // End Datepicker

    // Calcular antiguedad
    function calcularAntiguedad() {
      var fechaInicio = moment($('#fecha_inicio').val().trim(), 'DD/MM/YYYY');
      var fechaFin = moment($('#fecha_fin').val().trim(), 'DD/MM/YYYY');

      if (fechaInicio.isValid() && fechaFin.isValid()) {
        var ant_anios = fechaFin.diff(fechaInicio, 'year');
        fechaInicio.add(ant_anios, 'years');

        var ant_meses = fechaFin.diff(fechaInicio, 'months');
        fechaInicio.add(ant_meses, 'months');

        var ant_dias = fechaFin.diff(fechaInicio, 'days');
        fechaInicio.add(ant_dias, 'days');

        var antiguedadTexto = `${ant_anios} ${ant_anios === 1 ? 'año' : 'años'}, ${ant_meses} ${ant_meses === 1 ? 'mes' : 'meses'}, ${ant_dias} ${ant_dias === 1 ? 'día' : 'días'}`;

        $('#antiguedad_total').val(antiguedadTexto);
      } else {
        console.error('Fechas inválidas');
      }

      var antiguedadTiempo = {
        anios: ant_anios,
        meses: ant_meses,
        dias: ant_dias,
      };

      return antiguedadTiempo;
    }

    // Calcular indemnizacion
    function calcularIndenmizacion(antiguedadTiempo, salarioPorDia) {
      var aniosTiempo = antiguedadTiempo.anios;
      var mesesTiempo = antiguedadTiempo.meses;
      var diasTiempo = antiguedadTiempo.dias;

      // *** Primeros 3 años ***

      var indem_primerosTresAnio = 0;

      // Si antiguedad es menor o igual a 3 años
      if (aniosTiempo <= 3) {
        indem_primerosTresAnio = aniosTiempo * 30;
      }
      // Si antiguedad es mayor a 3 años
      else {
        indem_primerosTresAnio = 3 * 30;
      }

      // *** 4to año a + ***

      var indem_cuartoAnio = 0;

      // Si antiguedad es mayor o igual a 3 años
      if (aniosTiempo > 3) {
        indem_cuartoAnio = (aniosTiempo - 3) * 20;
      }
      // Si antiguedad es menor a 3 años
      else {
        indem_cuartoAnio = 0;
      }

      // *** Meses ***

      var indem_meses = 0;

      // Si antiguedad es mayor a 3 años
      if (aniosTiempo >= 3) {
        indem_meses = (20 / 12) * mesesTiempo;
      } else {
        indem_meses = (30 / 12) * mesesTiempo;
      }

      // *** Días ***

      var indem_dias = 0;

      // Si antiguedad es mayor a 3 años
      if (aniosTiempo >= 3) {
        indem_dias = (20 / 12 / 30) * diasTiempo;
      } else {
        indem_dias = (30 / 12 / 30) * diasTiempo;
      }

      var indem_totalDias = 0;
      indem_totalDias = indem_primerosTresAnio + indem_cuartoAnio + indem_meses + indem_dias; // Total días

      // Máximo 150 días
      if (indem_totalDias > 150) {
        indem_totalDias = 150;
      }

      var indemnizacionTotal = salarioPorDia * indem_totalDias;
      var indemnizacionText = formatter.format(indemnizacionTotal);
      $('#indemnizacion_total').val(indemnizacionText.replace('$', ''));
      $('#indemnizacion_dias').val(indem_totalDias.toFixed(2));

      return indemnizacionTotal;
    }

    // Calcular vacaciones
    function calcularVacaciones(salarioPorDia) {
      var vacacionesVal = $('#vacaciones').val().trim() || 0;
      var vacaciones = parseFloat(vacacionesVal) || 0;
      var vacacionesTotal = salarioPorDia * vacaciones;

      var vacionesText = formatter.format(vacacionesTotal);
      $('#vacaciones_total').val(vacionesText.replace('$', ''));

      return vacacionesTotal;
    }

    // Calcular aguinaldo
    function calcularAguinaldo(salarioMensual, antiguedadTiempo) {
      // ---------------------------- Nueva fórmula ----------------------------

      var aguinaldoTotal = 0;
      var aniosTiempo = antiguedadTiempo.anios;
      var mesesTiempo = antiguedadTiempo.meses;
      var diasTiempo = antiguedadTiempo.dias;

      var fechaInicio = moment($('#fecha_inicio').val().trim(), 'DD/MM/YYYY');
      var fechaFin = moment($('#fecha_fin').val().trim(), 'DD/MM/YYYY');

      var anioInicio = fechaInicio.year();
      var anioFin = fechaFin.year();

      var mesInicio = fechaInicio.month() + 1;
      var mesFin = fechaFin.month() + 1;

      var fecha31Dic = moment(`31/12/${anioInicio}`, 'DD/MM/YYYY');
      var C8 = moment($('#fecha_inicio').val().trim(), 'DD/MM/YYYY');

      var fecha01Mes = moment(`01/${mesFin}/${anioFin}`, 'DD/MM/YYYY');
      var C9 = moment($('#fecha_fin').val().trim(), 'DD/MM/YYYY');

      var Q23 = moment(`01/12/${anioFin - 1}`, 'DD/MM/YYYY');
      var C9_2 = moment($('#fecha_fin').val().trim(), 'DD/MM/YYYY');

      var fecha31Dic_2 = moment(`31/12/${anioFin}`, 'DD/MM/YYYY');
      var C8_2 = moment($('#fecha_inicio').val().trim(), 'DD/MM/YYYY');

      var C5 = salarioMensual;

      // Antiguedad V22 (Fecha Inicio con 31/Dic/Año Inicio)

      var dic31_anios = 0;
      var dic31_meses = 0;
      var dic31_dias = 0;

      if (C8.isValid() && fecha31Dic.isValid()) {
        dic31_anios = fecha31Dic.diff(C8, 'year');
        C8.add(dic31_anios, 'years');

        dic31_meses = fecha31Dic.diff(C8, 'months');
        C8.add(dic31_meses, 'months');

        dic31_dias = fecha31Dic.diff(C8, 'days');
        C8.add(dic31_dias, 'days');
      }

      // Antiguedad V23 (01/Mes Salida/Año Salida con Fecha Salida)

      var mes01_anios = 0;
      var mes01_meses = 0;
      var mes01_dias = 0;

      if (fecha01Mes.isValid() && C9.isValid()) {
        mes01_anios = C9.diff(fecha01Mes, 'year');
        fecha01Mes.add(mes01_anios, 'years');

        mes01_meses = C9.diff(fecha01Mes, 'months');
        fecha01Mes.add(mes01_meses, 'months');

        mes01_dias = C9.diff(fecha01Mes, 'days');
        fecha01Mes.add(mes01_dias, 'days');
      }

      // Antiguedad R23 (01/Dic/Año anterior con Fecha de salida)

      var dic01_anios = 0;
      var dic01_meses = 0;
      var dic01_dias = 0;

      if (Q23.isValid() && C9_2.isValid()) {
        dic01_anios = C9_2.diff(Q23, 'year');
        Q23.add(dic01_anios, 'years');

        dic01_meses = C9_2.diff(Q23, 'months');
        Q23.add(dic01_meses, 'months');

        dic01_dias = C9_2.diff(Q23, 'days');
        Q23.add(dic01_dias, 'days');
      }

      // Antiguedad S25 (Fecha Inicio con 31/Dic/Año de Salida)

      var dic31_2_anios = 0;
      var dic31_2_meses = 0;
      var dic31_2_dias = 0;

      if (C8_2.isValid() && fecha31Dic_2.isValid()) {
        dic31_2_anios = fecha31Dic_2.diff(C8_2, 'year');
        C8_2.add(dic31_2_anios, 'years');

        dic31_2_meses = fecha31Dic_2.diff(C8_2, 'months');
        C8_2.add(dic31_2_meses, 'months');

        dic31_2_dias = fecha31Dic_2.diff(C8_2, 'days');
        C8_2.add(dic31_2_dias, 'days');
      }

      // Si antiguedad es menor a 1 año Y año de inicio igual a año de salida
      // SI(Y(I7 < 1, (AÑO(C8) = AÑO(C9))), X24, V25)
      if (aniosTiempo < 1 && anioInicio === anioFin) {
        // Diferencia de días entre Fecha inicio y 31/Dic/Año Inicio
        var V22 = dic31_dias + 1;
        // Diferencia de días entre 01/Mes Salida/Año Salida y 31/Dic/Año Inicio
        var V23 = mes01_dias + 1;
        // Suma de diferencia de días
        var V24 = V22 + V23;

        // D/M 01-Jan
        var W23 = 0;

        if (V24 > 29) {
          W23 = V22 + V23 - 1;
        } else {
          W23 = V22 + V23;
        }

        // D/M 01-Jan
        var W24 = 0;

        if (W23 > 29) {
          // SIFECHA(C8, C9,  'ym') - 1
          W24 = mesesTiempo - 1;
        } else {
          // SIFECHA(C8, C9,  'ym')
          W24 = mesesTiempo;
        }

        // Escenarios X24
        aguinaldoTotal = (C5 / 12) * W24 + (C5 / 12 / 30) * W23;
      }
      // Si antiguedad es mayor a 1 año Y año de inicio distinto a año de salida
      else {
        //  *** Escenarios X22 ***

        var S23 = 0;
        // SIFECHA(Q23, C9, 'md') + 1
        S23 = dic01_dias + 1;

        var S25 = 0;
        // SIFECHA(C8, P25, 'md') + S23
        S25 = dic31_2_dias + S23;

        // Meses de aguinaldo (01-Dic)
        var R23 = 0;

        if (S25 > 29) {
          // SIFECHA(Q23, C9, 'ym')
          R23 = dic01_meses;
        } else {
          // SIFECHA(Q23, C9, 'ym')
          R23 = dic01_meses;
        }

        // Escenarios X22
        var X22 = 0;
        X22 = (C5 / 12) * R23 + (C5 / 12 / 30) * S23;

        //  *** Escenarios X23 ***

        // Diferencia de días entre Fecha inicio y 31/Dic/Año Inicio
        var V22 = dic31_dias + 1;
        // Diferencia de días entre 01/Mes Salida/Año Salida y 31/Dic/Año Inicio
        var V23 = mes01_dias + 1;
        // Suma de diferencia de días
        var V24 = V22 + V23;

        // D/M 01-Jan
        var W23 = 0;

        if (V24 > 29) {
          W23 = V22 + V23 - 1;
        } else {
          W23 = V22 + V23;
        }

        // D/M 01-Jan
        var W24 = 0;

        if (W23 > 29) {
          // SIFECHA(C8, C9,  'ym') - 1
          W24 = mesesTiempo - 1;
        } else {
          // SIFECHA(C8, C9,  'ym')
          W24 = mesesTiempo;
        }

        // Escenarios X23
        var X23 = 0;
        X23 = (C5 / 12) * W24 + (C5 / 12 / 30) * W23;

        // SI(Y(I7 < 1, (MES(C8) = MES(P25))), X23, X22) // P25 = 31-12-2020
        if (aniosTiempo < 1 && mesInicio === 12) {
          // V25
          aguinaldoTotal = X23;
        } else {
          // V25
          aguinaldoTotal = X22;
        }
      }

      // -------------------------- Fin Nueva fórmula --------------------------

      var aguinaldoText = formatter.format(aguinaldoTotal);
      $('#aguinaldo_total').val(aguinaldoText.replace('$', ''));

      return aguinaldoTotal;
    }

    // Calcular salario proporcional
    function calcularSalarioProporcional(salarioPorDia, modalidad) {
      var fechaFin = moment($('#fecha_fin').val().trim(), 'DD/MM/YYYY');
      var fechaFinMes = fechaFin.month() + 1;
      var fechaFinDia = fechaFin.date();
      var salarioProporcionalTotal = 0;

      // Si es quincenal
      if (modalidad === 'quincenal') {
        if (fechaFinDia > 15) {
          salarioProporcionalTotal = (fechaFinDia - 15) * salarioPorDia;
        } else {
          salarioProporcionalTotal = fechaFinDia * salarioPorDia;
        }
      }
      // Si es mensual
      else {
        // Condicion febrero
        if (fechaFinMes == 2) {
          if (fechaFinDia >= 28) {
            salarioProporcionalTotal = salarioPorDia * 30; // Se cuentan como 30 días
          } else {
            salarioProporcionalTotal = fechaFinDia * salarioPorDia;
          }
        } else {
          salarioProporcionalTotal = fechaFinDia * salarioPorDia;
        }
      }

      var salarioProporcionalText = formatter.format(salarioProporcionalTotal);
      $('#salario_proporcional').val(salarioProporcionalText.replace('$', ''));

      return salarioProporcionalTotal;
    }

    // Calcular INSS
    function calcularINSS(cantidad) {
      return cantidad * (7 / 100) || 0;
    }

    // Calcular IR
    function calcularIR(salaryAfterInss, anual = false) {
      var anualSalary = salaryAfterInss * 12 || 0;

      if (anual) {
        anualSalary = salaryAfterInss || 0;
      }

      var overExcess = 0;
      var applicablePercentage = 0;
      var baseTax = 0;

      var withoutOverExcess = 0;
      var withaAplicablePercentage = 0;
      var withBaseTax = 0;
      var monthlyIR = 0;

      if (anualSalary >= 0 && anualSalary <= 100000) {
        overExcess = 0;
        applicablePercentage = 0;
        baseTax = 0;

        withoutOverExcess = anualSalary - overExcess || 0;
        withaAplicablePercentage = withoutOverExcess * applicablePercentage || 0;
        withBaseTax = withaAplicablePercentage + baseTax || 0;
        monthlyIR = withBaseTax / 12;
      } else if (anualSalary > 100000 && anualSalary <= 200000) {
        overExcess = 100000;
        applicablePercentage = 15 / 100 || 0;
        baseTax = 0;

        withoutOverExcess = anualSalary - overExcess || 0;
        withaAplicablePercentage = withoutOverExcess * applicablePercentage || 0;
        withBaseTax = withaAplicablePercentage + baseTax || 0;
        monthlyIR = withBaseTax / 12;
      } else if (anualSalary > 200000 && anualSalary <= 350000) {
        overExcess = 200000;
        applicablePercentage = 20 / 100 || 0;
        baseTax = 15000;

        withoutOverExcess = anualSalary - overExcess || 0;
        withaAplicablePercentage = withoutOverExcess * applicablePercentage || 0;
        withBaseTax = withaAplicablePercentage + baseTax || 0;
        monthlyIR = withBaseTax / 12;
      } else if (anualSalary > 350000 && anualSalary <= 500000) {
        overExcess = 350000;
        applicablePercentage = 25 / 100 || 0;
        baseTax = 45000;

        withoutOverExcess = anualSalary - overExcess || 0;
        withaAplicablePercentage = withoutOverExcess * applicablePercentage || 0;
        withBaseTax = withaAplicablePercentage + baseTax || 0;
        monthlyIR = withBaseTax / 12;
      } else if (anualSalary > 500000) {
        overExcess = 500000;
        applicablePercentage = 30 / 100 || 0;
        baseTax = 82500;

        withoutOverExcess = anualSalary - overExcess || 0;
        withaAplicablePercentage = withoutOverExcess * applicablePercentage || 0;
        withBaseTax = withaAplicablePercentage + baseTax || 0;
        monthlyIR = withBaseTax / 12;
      }

      return monthlyIR;
    }

    // Calcular liquidación
    function calcularLiquidacion() {
      // Calcular Antiguedad
      var antiguedadTiempo = calcularAntiguedad() || {};

      var salarioMensual = 0;

      var tipoSalario = $('#modalida_salario option:selected').val().trim();

      // Si salario es fijo
      if (tipoSalario === 'fijo') {
        salarioMensual = parseFloat($('#salario').val().trim()) || 0;
      }
      // Si salario es variable
      else {
        var ultimosSalarios = [parseFloat($('#salario_1').val().trim()) || 0, parseFloat($('#salario_2').val().trim()) || 0, parseFloat($('#salario_3').val().trim()) || 0, parseFloat($('#salario_4').val().trim()) || 0, parseFloat($('#salario_5').val().trim()) || 0, parseFloat($('#salario_6').val().trim()) || 0];
        // salarioMensual = Math.max(...ultimosSalarios);

        salarioMensual = Math.max(...ultimosSalarios);
        var sumaSalarios = 0;

        $.each(ultimosSalarios, function () {
          sumaSalarios += this;
        });

        salarioMensual = sumaSalarios / 6;
      }

      var modalidad = $('#modalida_pago option:selected').val().trim();

      // if (modalidad === 'quincenal') {
      //   if (tipoSalario === 'fijo') {
      //     salarioMensual = salarioMensual * 2;
      //   }
      //   // Si salario es variable si ingresa en mensual
      //   else {
      //     salarioMensual = salarioMensual;
      //   }
      // }

      var salarioPorDia = salarioMensual / 30;

      // Calcular indemnización
      var indemnizacion = calcularIndenmizacion(antiguedadTiempo, salarioPorDia) || 0;

      // Calcular vacaciones
      var vacaciones = calcularVacaciones(salarioPorDia) || 0;

      // Calcular aguinaldo
      var aguinaldo = calcularAguinaldo(salarioMensual, antiguedadTiempo) || 0;

      // Calcular salario proporcional
      var salarioProporcional = calcularSalarioProporcional(salarioPorDia, modalidad) || 0;

      // Ingresos bruto
      var ingresosBrutos = 0;

      ingresosBrutos = indemnizacion + vacaciones + aguinaldo + salarioProporcional;
      var ingresosBrutosText = formatter.format(ingresosBrutos);
      $('#ingresos_brutos_total').val(ingresosBrutosText.replace('$', ''));

      // Calcular INSS vacaciones
      var inssVacaciones = calcularINSS(vacaciones) || 0;
      var inssVacacionesText = formatter.format(inssVacaciones);
      $('#inss_vacaciones_total').val(inssVacacionesText.replace('$', ''));

      // Calcular INSS salario proporcional
      var inssSalarioProporcional = calcularINSS(salarioProporcional) || 0;
      var inssSalarioProporcionalText = formatter.format(inssSalarioProporcional);
      $('#inss_salario_proporcional').val(inssSalarioProporcionalText.replace('$', ''));

      // Calcular INSS total
      var inssTotal = inssVacaciones + inssSalarioProporcional || 0;
      var inssTotalText = formatter.format(inssTotal);
      $('#inss_total').val(inssTotalText.replace('$', ''));

      // Calcular IR
      var inssMensualAplicado = calcularINSS(salarioMensual) || 0;
      var irMensualAplicado = calcularIR(salarioMensual - inssMensualAplicado) || 0;
      var irDiarioAplicado = irMensualAplicado / 30 || 0;

      // Calcular IR vacaciones
      var expectativaSalarioAnual = (salarioMensual - inssMensualAplicado) * 12 || 0;
      var irExpectativaSalarioAnual = calcularIR(expectativaSalarioAnual, true) * 12 || 0;

      var expectativaVacacionesAnual = (salarioMensual - inssMensualAplicado) * 12 + (vacaciones - inssVacaciones) || 0;
      var irExpectativaVacacionesAnual = calcularIR(expectativaVacacionesAnual, true) * 12 || 0;

      var irVacaciones = irExpectativaVacacionesAnual - irExpectativaSalarioAnual;

      var irVacacionesText = formatter.format(irVacaciones);
      $('#ir_vacaciones_total').val(irVacacionesText.replace('$', ''));

      // Calcular IR salario proporcional
      var DiasSalarioProporcional = moment($('#fecha_fin').val().trim(), 'DD/MM/YYYY').date();
      var irSalarioProporcional = DiasSalarioProporcional * irDiarioAplicado || 0;

      // Si es quincenal
      if (modalidad === 'quincenal') {
        if (DiasSalarioProporcional > 15) {
          irSalarioProporcional = (DiasSalarioProporcional - 15) * irDiarioAplicado || 0;
        }
      }

      var irSalarioProporcionalText = formatter.format(irSalarioProporcional);
      $('#ir_salario_proporcional').val(irSalarioProporcionalText.replace('$', ''));

      // Calcular IR total
      var irTotal = irVacaciones + irSalarioProporcional || 0;
      var irTotalText = formatter.format(irTotal);
      $('#ir_total').val(irTotalText.replace('$', ''));

      // Neto a recibir
      var netoTotal = ingresosBrutos - inssTotal - irTotal || 0;
      var netoText = formatter.format(netoTotal);
      $('#neto_recibir').val(netoText.replace('$', ''));
    }
    // End Calcular liquidación

    // Limpiar datos
    function limpiarDatos() {
      $('#salario').val('');
      $('#modalida_pago option:eq(0)').prop('selected', true);
      $('#fecha_inicio').val('');
      $('#fecha_fin').val('');
      $('#vacaciones').val('');

      $('#antiguedad_total').val('');
      $('#indemnizacion_dias').val('');
      $('#indemnizacion_total').val('');
      $('#vacaciones_total').val('');
      $('#aguinaldo_total').val('');
      $('#salario_proporcional').val('');
      $('#ingresos_brutos_total').val('');

      $('#inss_vacaciones_total').val('');
      $('#inss_salario_proporcional').val('');
      $('#inss_total').val('');
      $('#neto_recibir').val('');

      // Hide results
      $('#severance_pay_fields').hide(300);

      // Tipo de salario
      $('.salario-variable').hide();

      $('#modalida_salario option:eq(0)').prop('selected', true);
      $('#salario_1').val('');
      $('#salario_2').val('');
      $('#salario_3').val('');
      $('#salario_4').val('');
      $('#salario_5').val('');
      $('#salario_6').val('');
    }
    // End Limpiar datos

    // On click - Calculate
    $('#severance_pay_calculate').click(function () {
      if ($(this).data('action') === 'clean') {
        limpiarDatos();
        // Set button data attribute
        $(this).data('action', 'calculate');
        // Set button text
        $(this).text('Calcular ahora');
        // Focus on salary input
        $('#salario').focus();
        return;
      } else {
        var startDate = $('#fecha_inicio').val().trim() || '';
        var endDate = $('#fecha_fin').val().trim() || '';

        // Validar vacíos en fechas
        if (startDate === '') {
          $('#fecha_inicio').focus();
          alert('Selecionar el valor de fecha inicio');
          return false;
        }

        if (endDate === '') {
          alert('Selecionar el valor de fecha fin');
          $('#fecha_fin').focus();
          return false;
        }

        calcularLiquidacion();

        // Show results
        $('#severance_pay_fields').show(300);

        // Set button data attribute
        $(this).data('action', 'clean');
        // Set button text
        $(this).text('Empezar de nuevo');
        // Focus on salary input
        // $("#salario").focus();
        return;
      }
    });
    // End On click - Calculate
  });
</script>
